import { ModalComponent } from './../../../../components/modal/modal.component';
import { HttpClient } from '@angular/common/http';
import { Component, OnInit, ViewChild } from '@angular/core';
import { Observable } from 'rxjs';

import { Product } from '@webshop/api/entities';

@Component({
    selector: 'app-products',
    templateUrl: './products.component.html',
    styleUrls: ['./products.component.sass'],
})
export class ProductsComponent implements OnInit {
    constructor(private http: HttpClient) {}

    readonly api = 'http://localhost:3333/api/products';

    products!: Observable<Product[]>;

    @ViewChild('dialog')
    modal!: ModalComponent;

    getProducts() {
        return (this.products = this.http.get<Product[]>(this.api));
    }

    ngOnInit(): void {
        this.products = this.getProducts();
    }

    showModal(): void {
        this.modal.showModal();
    }
}
<div class="top">
    <div class="line-one">
        <h2>Produkter</h2>
        <menu>
            <li>
                <button (click)="showModal()">Tilføj produkt</button>
            </li>
        </menu>
    </div>
    <div class="line-two">
        <button>Tilgængelige</button>
        <button>Utilgængelige</button>
    </div>
</div>
<div class="bottom"></div>
<app-modal #dialog title="Tilføj Produkt">
    <form method="dialog">
        <div class="header">
            <h4>Tilføj produkt</h4>
        </div>
        <div class="form-group">
            <label for="name">Navn</label>
            <input type="text" id="name" name="name" required />
        </div>
        <div class="form-group">
            <label for="description">Beskrivelse</label>
            <input type="text" id="description" name="description" required />
        </div>
        <div class="form-group">
            <label for="price">Pris</label>
            <input type="text" id="price" name="price" required />
        </div>
        <div class="form-group">
            <label for="stock">Lagerantal</label>
            <input type="text" id="stock" name="stock" required />
        </div>
        <div class="form-group">
            <label for="available">Tilgængelighed</label>
            <input type="text" id="available" name="available" required />
        </div>
        <div class="form-group">
            <label for="image">Billede</label>
            <input type="file" id="image" name="image" required />
        </div>
        <div class="button-group">
            <button value="cancel">Fortryd</button>
            <button value="default">Opret</button>
        </div>
    </form>
</app-modal>
